/**
 * 将指定的 str 转换成以空格分隔的小写类型字符串，例如 `hello world`.
 * @since 1.0.0
 * @param [str = ''] (string) 带转换的字符串
 * @returns (string): 空格分割的小写类型字符串，例如 `hello world`
 * @example
 *
 * _.lowerCase('--Foo-Bar--');
 * // => 'foo bar'
 *
 * _.lowerCase('fooBar');
 * // => 'foo bar'
 *
 * _.lowerCase('__FOO_BAR__');
 * // => 'foo bar'
 */
export default function lowerCase(str?: any): string;
