/**
 * 将整个字符串转换成小写字母返回，跟 String#toLowerCase 基本类似
 *
 * @since 1.0.0
 *
 * @param [str=''] (string) 待转换的字符串
 * @returns (string): 返回的小写字符串。
 *
 * @example
 * _.toLower('--Foo-Bar--');
 * // => '--foo-bar--'
 *
 * _.toLower('fooBar');
 * // => 'foobar'
 *
 * _.toLower('__FOO_BAR__');
 * // => '__foo_bar__'
 */
export default function toLower(str?: string): string;
